    private void BTceseActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BTceseActionPerformed
        // TODO add your handling code here:
        if(ctrl.DevolverFechaJDateChooser(DCcese).trim().length()==0)
        {
            ctrl.mensaje.MensajeAlerta("Ud. no registró la fecha de Cese.");
        }
        else
        {
            int resp=ctrl.mensaje.MsjeConfirmacion("Está seguro que desea cesar al trabajador.\n"+TXnombres.getText().trim());
            if(resp==0)
            {
                ctrl.ActualizarData("ficha_datos","fecha_cese='"+ctrl.DevolverFechaJDateChooser(DCcese)+"',estado_ficha=2", "id_ficha='"+TXficha.getText().trim()+"'");
                ctrl.ActualizarData("personal", "estado=3", "dni='"+TXdni.getText().trim()+"'");
                IFRM_Ficha_Datos.BTactualizar.doClick();
                MDI_PRINCIPAL.BTactualizar.doClick();
                Reporte(TXficha.getText().trim(), TXdni.getText().trim());
                this.dispose();
            }
        }
    }//GEN-LAST:event_BTceseActionPerformed

