    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        if(TBficha.getRowCount()==0)
        {
            ctrl.mensaje.MensajeAlerta("No hay nada que visualizar.");
            Cancelar();
        }
        else if(TBficha.getSelectedRow()==-1)
        {
            ctrl.mensaje.MensajeAlerta("Ud. no seleccionÃ³ ninguna ficha");
            Cancelar();
        }
        else if(TBficha.getSelectedRowCount()>1)
        {
            ctrl.mensaje.MensajeAlerta("Seleccione solo una ficha por favor.");
            Cancelar();
        }
        else
        {
            this.dispose();
            reporte.Reporte_1Parametro_Imagen("RPT_Certificado.jasper", "p_codigo", ctrl.ValorTabla(TBficha,0), "p_logo", "/PQ_IMAGENES/LogoSantisimaCruzEdit.png");
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void TXbuscarKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_TXbuscarKeyTyped
        // TODO add your handling code here:
        ctrl.SoloNumeroEnteros(TXbuscar);
        ctrl.Limittxt(TXbuscar, evt, 7);
        ctrl.BuscarJtable(TXbuscar, TBficha, 4);
    }//GEN-LAST:event_TXbuscarKeyTyped

