    private void TXbuscarKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_TXbuscarKeyTyped
        // TODO add your handling code here:
        if (CBfiltrar.getSelectedIndex() == 0) {
            ctrl.BuscarJtable(TXbuscar, TBOfertas, 1);
        } else if (CBfiltrar.getSelectedIndex() == 1) {
            ctrl.BuscarJtable(TXbuscar, TBOfertas, 2);
        } else if (CBfiltrar.getSelectedIndex() == 2) {
            ctrl.BuscarJtable(TXbuscar, TBOfertas, 3);
        }
    }//GEN-LAST:event_TXbuscarKeyTyped

