    private void Cancelar() {
        ctrl.VisualizarDataEscogidaEnTabla("oferta_laboral_id_oferta,oferta_laboral_fecha_publicacion,cargo_descripcion,oferta_laboral_lugar,oferta_laboral_estado", "v_oportunidad_laboral", TBOfertas, "");
        ctrl.LimpiarTabla(TBpostulantes);
        CBfiltrar.setSelectedIndex(0);
        TXbuscar.setText("");

    }

    private void ConsultarPostulante() {
        if (TBOfertas.getRowCount() == 0) {
            ctrl.mensaje.MensajeAlerta("No hay ninguna fila para consultar");
        } else if (TBOfertas.getSelectedRow() == -1) {
            ctrl.mensaje.MensajeAlerta("Ud. no seleccionó una fila.");
        } else if (TBOfertas.getSelectedRowCount() > 1) {
            ctrl.mensaje.MensajeAlerta("Sólo debe seleccionar una fila a consultar,");
        } else {
            ctrl.VisualizarDataEscogidaEnTabla("detalle_oferta_id_oferta,detalle_oferta_id_postulante,postulante_DNI,postulante_nombres,postulante_apellidos,postulante_direccion_cv,postulante_cargo,detalle_oferta_FECHA", "v_detalle_oferta", TBpostulantes, "WHERE detalle_oferta_id_oferta='" + ctrl.ValorTabla(TBOfertas, 0) + "'");
        }
    }

    private void ExportarExcel() {
        if (TBpostulantes.getRowCount() == 0) {
            ctrl.mensaje.MensajeAlerta("No hay nada para exportar");
        } else {
            JFileChooser seleccionar = new JFileChooser();
            File archivo;
            if (seleccionar.showDialog(null, "Exportar Excel") == JFileChooser.APPROVE_OPTION) {
                archivo = seleccionar.getSelectedFile();
                int cantFila = TBpostulantes.getRowCount();
                int cantColumna = TBpostulantes.getColumnCount();
                Workbook wb;
                wb = new XSSFWorkbook();
                Sheet hoja = wb.createSheet("  ");

                try {
                    for (int i = -1; i < cantFila; i++) {
                        Row fila = hoja.createRow(i + 1);
                        for (int j = 0; j < cantColumna; j++) {
                            Cell celda = fila.createCell(j);
                            if (i == -1) {
                                celda.setCellValue(String.valueOf(TBpostulantes.getColumnName(j)));
                            } else {
                                celda.setCellValue(String.valueOf(TBpostulantes.getValueAt(i, j)));
                            }
                            wb.write(new FileOutputStream(archivo + ".xlsx"));
                        }
                    }
                    JOptionPane.showMessageDialog(null, "Exportacion exitosa");
                } catch (Exception e) {
                    JOptionPane.showMessageDialog(null, "Vuelve a intentarlo");
                }

            } else {

            }
        }
    }
