    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        if (TX1.getText().trim().length() == 0) {
            ctrl.mensaje.MensajeAlerta("No hay ninguna ficha seleccionada.");
        } else {
            if (TXcantidad.getText().trim().length() == 0) {
                ctrl.mensaje.MensajeAlerta("Ingrese la cantidad de EPP entregado.");
                TXcantidad.requestFocus();
            } else {
                if (ctrl.ValidarRegistroTabla(jTable1)) {
                    ctrl.GrabarData("detalle_entrega", "ficha_datos_id_ficha, EPP_idepp, cantidad, fecha", "'" + TX1.getText().trim() + "','" + ctrl.ValorTabla(jTable1, 0) + "','" + TXcantidad.getText().trim() + "','" + ctrl.Fecha() + " " + ctrl.Hora() + "'");
                    Visualizar();
                    TXcantidad.setText("");
                    TXbuscar.requestFocus();
                }
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

