    private void BTdirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BTdirActionPerformed
        // TODO add your handling code here:
        ctrl.AbrirRuta(TXdireccion, "csv", "csv");
    }//GEN-LAST:event_BTdirActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        if(CBtablas.getSelectedIndex()==-1)
        {
            ctrl.mensaje.MensajeAlerta("Seleccione una tabla del combo.");
            CBtablas.requestFocus();
        }
        else if(TXdireccion.getText().trim().length()==0)
        {
            ctrl.mensaje.MensajeAlerta("Registre un directorio de con formato CSV");
            BTdir.doClick();
        }
        else
        {
            ctrl.Excel_A_MySQL(TXdireccion.getText().trim(), CBtablas.getSelectedItem().toString().trim());
            this.dispose();
        }
    }//GEN-LAST:event_jButton2ActionPerformed

