    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        // TODO add your handling code here:
        if(TBreq.getSelectedRow()==-1)
        {
            ctrl.mensaje.MensajeAlerta("Ud. no seleccionó ninguna fila");
        }
        else
        {
            Reporte(ctrl.ValorTabla(TBreq, 0));
        }
    }//GEN-LAST:event_jButton5ActionPerformed

    private void TXapKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_TXapKeyReleased
        // TODO add your handling code here:
        try {
            int nro=Integer.parseInt(ctrl.ValorTabla(TBreq, 3));
            int ap=Integer.parseInt(TXap.getText().trim());
            TXdes.setText(""+(nro-ap));
        } catch (NumberFormatException e)
        {
            e.getMessage();
        }catch(ArrayIndexOutOfBoundsException e)
        {
            e.getMessage();
        }
        
    }//GEN-LAST:event_TXapKeyReleased

