    private void Buscar() {
        if (TXreq.getText().trim().length()==0)
        {
            ctrl.mensaje.MensajeAlerta("Ud. no ingresó nigún número de requerimiento.");
            Cancelar();
        }
        else
        {
            VerTabla();
        }
//        else(TBreq.getRowCount()==0)
//        {
//            ctrl.mensaje.MensajeAlerta("Este número de requerimiento ya fue registrado o no existe");
//            Cancelar();            
//        }
        
    }
    private void Cancelar()
    {
        TXreq.setText("");
        TXap.setText("");
        TXdes.setText("");
        TXob.setText("");
        ctrl.LimpiarTabla(TBreq);
        TXreq.requestFocus();
    }
    private void VerTabla()
    {
        ctrl.VisualizarDataEscogidaEnTabla("requerimiento_personal_id_req_per,requerimiento_personal_fecha_emision,requerimiento_personal_fecha_recepcion,requerimiento_personal_nro_personas_req,requerimiento_personal_estado,requerimiento_personal_fecha_aprobacion,obra_planta_descripcion,usuario_nombres_apellidos","v_requerimiento", TBreq, "where requerimiento_personal_id_req_per='"+TXreq.getText().trim()+"'");
    }
    private void Reporte(String codigo)
    {
        reporte.Reporte_1Parametro_Imagen("RPT_Hoja_Requerimiento.jasper", "p_codigo", codigo, "p_logo", "/PQ_IMAGENES/LogoSantisimaCruzEdit.png");
    }
    private void Noescribe(KeyEvent evt)
    {
        if(TBreq.getSelectedRow()==-1)
        {
            ctrl.NoEscribe(evt);
        }
    }
