    private void Induci() 
    {
        if (ctrl.DevolverFechaJDateChooser(DTfecha).trim().length()*TXdni.getText().trim().length()*TXrealizado.getText().trim().length()==0)
        {
            ctrl.mensaje.MensajeAlerta("Datos imcompletos.");
            DTfecha.requestFocus();
        }
        else if(TB_tema.getSelectedRow()==-1)
        {
            ctrl.mensaje.MensajeAlerta("Seleccione un tema de inducción.");
        }
        else
        {
            ctrl.GrabarData("induccion_puesto", "id_tema,dni,fecha_induccion,observaciones,estado,realizado_por", "'"+ctrl.ValorTabla(TB_tema, 0)+"','"+TXdni.getText().trim()+"','"+ctrl.DevolverFechaJDateChooser(DTfecha)+"'"
                    + ",'"+TXobs.getText().trim()+"','REALIZADO','"+TXrealizado.getText().trim()+"'");
            Filtrar();
        }
    }
    private void Filtrar()
    {
        if (TXdni.getText().trim().length()==0)
        {
            ctrl.mensaje.MensajeAlerta("Seleccione un personal.");
            BTselect.doClick();
        }
        else
        {
            ctrl.VisualizarDataEscogidaEnTabla("id_tema,dni,fecha_induccion", "induccion_puesto", TBinduccion, "where dni='"+TXdni.getText().trim()+"'");
            DTfecha.setDate(null);
            TXrealizado.setText("");
            TXobs.setText("");
        }
    }
