    private void Guardar() {
        String des, ab;
        des = TX1.getText().trim();
        ab = TX2.getText().trim();
        if (des.trim().length() == 0) {
            ctrl.mensaje.MensajeAlerta("Ud. no ingresó ninguna descripción de la Unidad de Medida.");
            Cancelar();
            TX1.requestFocus();
        } else if (ab.trim().length() == 0) {
            ctrl.mensaje.MensajeAlerta("Ud. no ingresó ninguna abreviatura de la Unidad de Medida.");
            Cancelar();
            TX2.requestFocus();
        } else {
            if (ctrl.mensaje.MsjeConfirmacion("Está seguro que dese guardar esta unidad?") == 0) {
                ctrl.GrabarData("unidad", "descripcion,abreviatura", "'" + des + "','" + ab + "'");
                this.dispose();
            } else {
                ctrl.mensaje.MensajeSimple("Proceso Cnacelado.");
                this.dispose();
            }
        }
    }

    private void Cancelar() {
        TX1.setText("");
        TX2.setText("");
    }
