    private void Generar()
    {
        if (ctrl.DevolverFechaJDateChooser(DTfecha).trim().length()*TXhoras.getText().trim().length()*TXlugar.getText().trim().length()==0)
        {
            ctrl.mensaje.MensajeAlerta("Datos incompletos.");
            DTfecha.requestFocus();
        }
        else
        {
            if (ctrl.mensaje.MsjeConfirmacion("Desea Generar esta capacitación.")==0) 
            {
                ctrl.GrabarData("capacitacion", "modo,fecha,hras_capacitacion,lugar,objetivo_curso,material_adjunto,modo_evaluacion", "'"+CBmodo.getSelectedItem().toString().trim()+"','"+ctrl.DevolverFechaJDateChooser(DTfecha)+"',"
                    + "'"+TXhoras.getText().trim()+"','"+TXlugar.getText().trim()+"','"+TXobjetivo.getText().trim()+"','"+CBmaterial.getSelectedItem().toString().trim()+"','"+CBevaluacion.getSelectedItem().toString().trim()+"'");
                Cancelar1();
            }
            else
                
            {
                ctrl.mensaje.MensajeSimple("Proceso cancelado.");
                this.dispose();
            }
        }
        
    }
    
    private void Cancelar1()
    {
        TXcodigo.setText(ctrl.UltimoCodigo("id_capacitacion", "capacitacion"));
        CBmodo.setSelectedIndex(0);
        DTfecha.setDate(null);
        TXhoras.setText("");
        TXlugar.setText("");
        TXobjetivo.setText("");
        CBmaterial.setSelectedIndex(0);
        CBevaluacion.setSelectedIndex(0);
        BTgenerar.setEnabled(false);
    }

    private void Detallar()
    {
        if (TXcodigo.getText().trim().length()*TXdni.getText().trim().length()*TXnota.getText().trim().length()==0)
        {
            ctrl.mensaje.MensajeAlerta("Datos incompletos.\nVerifique por favor");     
        }
        else
        {
            if (ctrl.mensaje.MsjeConfirmacion("Desea detallar este item?")==0) 
            {
                ctrl.GrabarData("detalle_capacitacion", "id_capacitacion,dni,resultado,nota", "'"+TXcodigo.getText().trim()+"','"+TXdni.getText().trim()+"','"+CBresultado.getSelectedItem().toString().trim()+"','"+TXnota.getText().trim()+"'");
                Cancelar2();
            }
            else
            {
                Cancelar2();
            }            
        }
    }
    private void Cancelar2()
    {
        TXdni.setText("");
        CBresultado.setSelectedIndex(0);
        TXnota.setText("");
        ctrl.VisualizarEnTabla("detalle_capacitacion", TBdetalle, "where id_capacitacion='"+TXcodigo.getText().trim()+"'");
        BTdni.doClick();
    }

    private void Terminar()
    {
        if (TBdetalle.getRowCount()==0)
        {
            ctrl.mensaje.MensajeAlerta("No puede terminar el proceso ya que no esta registrado ningún elemento.\nInhgrese almenos un elemento.");
            BTdni.doClick();
        }
        else
        {
            reporte.Reporte_1Parametro_Imagen("RPT_Capacitacion.jasper", "p_codigo", TXcodigo.getText().trim(), "p_logo", "/PQ_IMAGENES/LogoSantisimaCruzEdit.png");
            this.dispose();
        }
    }

    private void Quitar()
    {
        if(TBdetalle.getSelectedRow()==-1)
        {
            ctrl.mensaje.MensajeAlerta("Seleccione una fila.");
        }
        else
        {
            ctrl.EliminarData("detalle_capacitacion", "where id_capacitacion='"+ctrl.ValorTabla(TBdetalle, 0)+"' and dni='"+ctrl.ValorTabla(TBdetalle, 1)+"'");
            Cancelar2();
        }
    }
