    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        if (CB1.getSelectedIndex() == -1) {
            ctrl.mensaje.MensajeAlerta("Por favor seleccione un indice.");
        } else {
            ctrl.VisualizarDataEscogidaEnTabla("ficha, dni, ap_paterno, ap_materno, nombres, cargo, obra, estado_ficha, estado_obra", "v_ficha_epp", jTable1, " WHERE obra='" + CB1.getSelectedItem().toString().trim() + "' and estado_ficha='VIGENTE'");
            LB1.setText("Nro. Trabajadores: " + ctrl.ContarFilasTablasSQL("v_ficha_epp", "WHERE obra='" + CB1.getSelectedItem().toString().trim() + "' and estado_ficha='VIGENTE' "));
        }

    }//GEN-LAST:event_jButton1ActionPerformed

    private void jTextField1KeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextField1KeyReleased
        // TODO add your handling code here:
        if (CB2.getSelectedIndex() == 0) {
            ctrl.BuscarJtable(jTextField1, jTable1, 1);
        } else {
            ctrl.BuscarJtable(jTextField1, jTable1, 2);
        }
    }//GEN-LAST:event_jTextField1KeyReleased

