    private void jMenuItem1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem1ActionPerformed
        // TODO add your handling code here:
        DLG_Detalle_Entrega_EPP dlg = new DLG_Detalle_Entrega_EPP(null, true);
        dlg.TX1.setText(ctrl.ValorTabla(jTable1, 0));
        dlg.TX2.setText(ctrl.ValorTabla(jTable1, 2) + " " + ctrl.ValorTabla(jTable1, 3) + " " + ctrl.ValorTabla(jTable1, 4));
        dlg.TX3.setText(ctrl.ValorTabla(jTable1, 1));
        dlg.Visualizar();
        dlg.setVisible(true);
    }//GEN-LAST:event_jMenuItem1ActionPerformed

    private void jMenuItem2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem2ActionPerformed
        // TODO add your handling code here:
        if (ctrl.ValidarRegistroTabla(jTable1)) {
            int res = Integer.parseInt(ctrl.ContarFilasTablasSQL("v_entrega_epp", "where ficha='" + ctrl.ValorTabla(jTable1, 0) + "'"));
            if (res > 0) {
                rpt.Reporte_1Parametro_Imagen("RPT_Entraga_EPP.jasper", "p_ficha", ctrl.ValorTabla(jTable1, 0), "p_logo", "/PQ_IMAGENES/LogoSantisimaCruzEdit.png");
            }else{
                ctrl.mensaje.MensajeAlerta("El Trabajador no cuenta con Entregas de EPP.");
            }
        }
    }//GEN-LAST:event_jMenuItem2ActionPerformed


