    private void Generar()
    {
        if(TXpersonal.getText().trim().length()==0)
        {
            ctrl.mensaje.MensajeAlerta("Ud. no selecciono ningÃºn personal.");
            BTpersonal.doClick();
        }
        else if(TXobra.getText().trim().length()==0)
        {
            ctrl.mensaje.MensajeAlerta("Ud. no selecciono ninguna obra.");
            BTobra.doClick();
        }
        else if(ctrl.DevolverFechaJDateChooser(DCf1).trim().length()==0)
        {
            ctrl.mensaje.MensajeAlerta("Ud. no seleccionÃ³ la fecha.");
        }
        else if(CBcargo.getSelectedIndex()==-1)
        {
            ctrl.mensaje.MensajeAlerta("Ud. no seleccionÃ³ el cargo del personal.");
            CBcargo.requestFocus();
        }
        else
        {
            String usu=ctrl.ObtenerCodigo("usuario", "nick", MDI_PRINCIPAL.TXnick.getText().trim(), 1);
            ctrl.GrabarData("ficha_datos","fecha_ingreso,estado_ficha,observacion,entrega_certificado,dni,id_usuario,id_obra_planta,cargo", "'"+ctrl.DevolverFechaJDateChooser(DCf1)+"',1,'"+TXobservacion.getText().trim()+"',"
                    + "2,'"+TXpersonal.getText().trim()+"','"+usu+"','"+TXobra.getText().trim()+"','"+CBcargo.getSelectedItem().toString().trim()+"'");
            ctrl.ActualizarData("personal", "estado=2", "dni='"+TXpersonal.getText().trim()+"'");
            Reporte(ctrl.UltimoCodigo("id_ficha", "ficha_datos"), TXpersonal.getText().trim());
            Cancelar();            
        }
    }
    private void Cancelar()
    {
        TXpersonal.setText("");
        CBcargo.setSelectedIndex(-1);
        TXobra.setText("");
        DCf1.setDate(null);
        TXobservacion.setText("");
        TXbuscar.setText("");
        ctrl.VisualizarDataEscogidaEnTabla("ficha_datos_id_ficha,ficha_datos_fecha_ingreso,ficha_datos_estado_ficha,ficha_datos_entrega_certificado,"
                + "ficha_datos_dni,obra_planta_descripcion", "v_ficha", TBficha, "where ficha_datos_estado_ficha='VIGENTE'");
    }
    private void Reporte(String codigo,String dni)
    {
        reporte.Reporte_2Parametro_Imagen("RPT_Ficha_Registro.jasper", "p_codigo", codigo, "p_dni", dni, "p_logo","/PQ_IMAGENES/LogoSantisimaCruzEdit.png");
    }
    private void Reporte2(String codigo)
    {
        reporte.Reporte_1Parametro_Imagen("RPT_Fotocheck.jasper","p_ficha", codigo,"p_logo", "/PQ_IMAGENES/LogoSantisimaCruzEdit.png");
    }
