    private void ActualizarFichaDatos() {
        ctrl.ActualizarData("personal", "estado_exa_med=3", "(f_rev_exa_med - curdate()) <= 0");
        ctrl.ActualizarData("oferta_laboral", "estado='CULMINADO'", "(fecha_cierre - curdate()) <= 0");
    }
    private void Notificacion()
     {
            if(Integer.parseInt(ctrl.ContarFilasTablasSQL("personal","where datediff(f_rev_exa_med,curdate())<=15 and datediff(f_rev_exa_med,curdate())>0"))>=1 || Integer.parseInt(ctrl.ContarFilasTablasSQL("personal","where datediff(f_rev_exa_med,curdate())<=0"))>=1)
            {
                net.sf.jcarrierpigeon.Notification obj=new net.sf.jcarrierpigeon.Notification(new FRM_Alertas(), WindowPosition.BOTTOMRIGHT, 0, 0, 15000);
               NotificationQueue val=new NotificationQueue();
               val.add(obj);
               FRM_Alertas.LBcontador.setText(ctrl.ContarFilasTablasSQL("personal","where datediff(f_rev_exa_med,curdate())<=15 and datediff(f_rev_exa_med,curdate())>0"));
               FRM_Alertas.LBcontador1.setText(ctrl.ContarFilasTablasSQL("personal","where datediff(f_rev_exa_med,curdate())<=0"));
            }        
     }

