    private void Confirmar() {
        if (CBalmacen.getSelectedIndex() == -1) {
            ctrl.mensaje.MensajeAlerta("Ud. no seleccionó un almacén");
            CBalmacen.requestFocus();
        } else {
            try {
                String a, b, c, d, e, f, g, h;
                for (int i = 0; i < TBdetalle_orden.getRowCount(); i++) {
                    a = LBmov.getText().trim();
                    b = TBdetalle_orden.getValueAt(i, 0).toString().trim();
                    c = TBdetalle_orden.getValueAt(i, 1).toString().trim();
                    d = TBdetalle_orden.getValueAt(i, 2).toString().trim();
                    e = TBdetalle_orden.getValueAt(i, 3).toString().trim();
                    f = "";
                    g = "";
                    h = ctrl.ObtenerCodigo("almacen", "descripcion", CBalmacen.getSelectedItem().toString().trim(), 1);
                    String[] datos = {a, b, c, d, e, f, g, h};
                    ctrl.dtm = (DefaultTableModel) DLG_Detalle_Movimientos.TBdetalle.getModel();
                    ctrl.dtm.addRow(datos);
                }
            } catch (NullPointerException e) {
                e.getMessage();
            }

            this.dispose();
        }
    }
