    private void Guardar() {
        if (TXcod.getText().trim().length() == 0) {
            ctrl.mensaje.MensajeError("No hay ninguna obra seleccionada.");
            BTobra.doClick();
        } else if (TBmaterial.getSelectedRowCount() == 0) {
            ctrl.mensaje.MensajeError("Seleccione un material.");
        } else if (txparcial.getText().trim().length() == 0 || Double.parseDouble(TXcantidad.getText().trim()) <= 0) {
            ctrl.mensaje.MensajeError("La cantidad no puede ser 0");
            Limpiar();
        }else{
            ctrl.GrabarData("detalle_presupuesto", "id_obra,id_material,cantidad,precio,parcial,id_unidad","'"+TXcod.getText().trim()+"','"+ctrl.ValorTabla(TBmaterial,0)+"','"+TXcantidad.getText().trim()+"','"+TXprecio.getText().trim()+"','"+txparcial.getText().trim()+"','"+ctrl.ObtenerCodigo("unidad", "descripcion", CBunidad.getSelectedItem().toString().trim(), 1)+"'");
            Limpiar();
        }
    }

    private void Limpiar() {
        TXbuscar_material.setText("");
        ctrl.VisualizarEnTabla("material", TBmaterial, "");
        CBunidad.setSelectedIndex(-1);
        TXcantidad.setText("");
        TXprecio.setText("");
        txparcial.setText("");
    }

    private void Calcular() {
        try {
            Double cal;
            cal = Double.parseDouble(TXcantidad.getText().trim()) * Double.parseDouble(TXprecio.getText().trim());
            txparcial.setText("" + ctrl.Redondear(cal, 2));
        } catch (NumberFormatException e) {
            e.getMessage();
            txparcial.setText("");
        }

    }
