    public void Limpiar()
    {
        DTfecha.setDate(null);
        CBtipo.setSelectedIndex(0);
        CBdocumento.setSelectedIndex(0);
        TXmotivo.setText("");
        TXnro.setText("");
        TXbuscar.setText("");
        CHBopcion.doClick();
        ctrl.VisualizarDataEscogidaEnTabla("id_movimiento,fecha,tipo_movimiento,documento,nro_documento,estado", "movimiento", TBmovimiento, "where estado='EMITIDO'");
    }

