    private void CHBopcionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CHBopcionActionPerformed
        // TODO add your handling code here:
        if(CHBopcion.isSelected())
        {
            BTobra.setEnabled(true);
            BTsolicitante.setEnabled(true);
            TXmotivo.setEditable(false);
            TXmotivo.setText("");
        }
        else
        {
            BTobra.setEnabled(false);
            BTsolicitante.setEnabled(false);
            TXmotivo.setEditable(true);
            TXmotivo.setText("");
        }
    }//GEN-LAST:event_CHBopcionActionPerformed

