    private void Guardar() {
        if(DTfecha.getDate()==null)
        {
            ctrl.mensaje.MensajeAlerta("Ud no seleccionó la fecha de movimiento.");
        }
        else if(TXmotivo.getText().trim().length()==0)
        {
            ctrl.mensaje.MensajeAlerta("Ud. no registró ninguna descripción del movimiento");
        }
        else
        {
            int resp=ctrl.mensaje.MsjeConfirmacion("Está seguro de emitir este movimiento?");
            if(resp==0)
            {
                ctrl.GrabarData("movimiento","fecha,tipo_movimiento,motivo,documento,nro_documento,estado", "'"+ctrl.DevolverFechaJDateChooser(DTfecha)+"','"+CBtipo.getSelectedItem().toString().trim()+"','"+TXmotivo.getText().trim()+"','"+CBdocumento.getSelectedItem().toString().trim()+"','"+TXnro.getText().trim()+"',1");
                Limpiar();
            }
            else
            {
                Limpiar();                        
            }           
        }
        
    }

    private void Cancelar() {
        Limpiar();        
    }

    private void Detallar() {
        if(TBmovimiento.getRowCount()==0)
        {
            ctrl.mensaje.MensajeAlerta("No hay niguna fila para detallar.");
            Limpiar();
        }
        else if(TBmovimiento.getSelectedRow()==-1)
        {
            ctrl.mensaje.MensajeAlerta("Ud. no seleccionó ninguna fila.");
            Limpiar();
        }
        else if(TBmovimiento.getSelectedRowCount()>1)
        {
            ctrl.mensaje.MensajeAlerta("Seleccione solo una fila.");
            Limpiar();
        }
        else
        {
            DLG_Detalle_Movimientos dlg=new DLG_Detalle_Movimientos(null, true);
            dlg.LBcodigo.setText(TBmovimiento.getValueAt(TBmovimiento.getSelectedRow(), 0).toString().trim());
            dlg.LBmovimiento.setText(TBmovimiento.getValueAt(TBmovimiento.getSelectedRow(), 2).toString().trim());
            if(ctrl.ValorTabla(TBmovimiento,3).equalsIgnoreCase("NRO DE ORDEN DE COMPRA"))
            {
                DLG_Detalle_Movimientos.TXorden.setText(ctrl.ValorTabla(TBmovimiento, 4));
            }
            dlg.setVisible(true);
        }
    }
